    (select owner, constraint_name , table_name 
    from all_constraints
    where owner = 'DBA_BV')
    loop
     execute immediate 'ALTER TABLE '||cur.owner||'.'||cur.table_name||' MODIFY CONSTRAINT "'||cur.constraint_name||'" DISABLE ';
